Page._content:
.index-section {
  $base-width: 500px;

  .logo-container {
    max-width: $base-width;
    display: block;
    margin: 50px auto;
    position: relative;
    overflow: hidden;
    background-color: black;

    canvas, .logo {
      display: block;
      width: 100%;
    }

    canvas {
      position: absolute;
      top: 0;
      left: 0;
      z-index: 0;
    }

    .logo {
      transform: scale(1.1); // this removes the tiny line around
    }
  }
}

